CREATE TABLE IF NOT EXISTS `#!-PREFIX-!#events` ( `ID` int(100) NOT NULL AUTO_INCREMENT,  `name` varchar(100) NOT NULL,  `date` varchar(100) NOT NULL,  `enddate` varchar(100) NOT NULL,  `start` varchar(100) NOT NULL,  `end` varchar(100) NOT NULL,  `level` varchar(100) NOT NULL,  `description` longtext NOT NULL,  `user` int(100) NOT NULL,  `options` text NOT NULL COMMENT 'Hier stehen Optionen (noch nicht genauer spezifiziert)',   `display` text NOT NULL COMMENT 'Auf welchen Seiten soll das angezeigt werden?',   `time` int(100) NOT NULL,   PRIMARY KEY (`ID`),   KEY `name` (`name`,`date`),  FULLTEXT KEY `description` (`description`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1  ;